import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addService } from '../actions/serviceActions';
import { Modal, Button } from 'antd';

import uuid from 'uuid'

class serviceModal extends Component {
    state = {
        visible: false,
        name: ''
    }
    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const newService = {
            id: uuid(),
            name: this.state.name
        }

        // Add service via addServiceAction
        this.props.addService(newService);

    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        Name:
          <input type="text" value={this.state.name} onChange={this.handleChange} />        </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }
}
const mapStateToProps = state => ({
    service: state.service
})

export default connect(mapStateToProps, { addService })(serviceModal)
