import React, { useState, useEffect } from "react";
import "./services.scss";
import axios from "axios";

export default function Services() {
  const [services, setServices] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [fullDebt, setFullDebt] = useState(0);

  const updatePricing = (e) => {
    //let newDebt = services.debt;
    //let time = new Date().getTime();
    //let updateService = {};
    //if (services.active) {
    //  newDebt += ((time - services.timeActivated) / 1000) * services.price;
    //  updateService = {
    //    active: !services.active,
    //    debt: newDebt,
    //    timeDeactivated: time
    //  }
    //}
    //
    //updateService = {
    //  active: !services.active,
    //  timeActivated: time
    //}
    //console.log(updateService);
    console.log(e.services.price)
    //axios.put(`/api/service/${services._id}`,{active:true}).then((err)=>console.log(err))
  }


  //useEffect(() => {
  //  const intervalId = setInterval(() => {
  //    let currentDebt = pricing.reduce((a, b) => ({ debt: a.debt + b.debt }));
  //    currentDebt = currentDebt.debt;
  //    pricing.map((price, index) => {
  //      if (price.time && price.active) {
  //        currentDebt +=
  //          ((new Date().getTime() - price.time) / 1000) *
  //          services[index].price;
  //      }
  //    });
  //    setFullDebt(Math.round((currentDebt + Number.EPSILON) * 100) / 100);
  //  }, 5000);
  //  return () => clearInterval(intervalId);
  //});

  useEffect(() => {
    fetch("/api/service")
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setServices(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return (
      <div>
        <h1>Services</h1>
        <p>Loading...</p>
      </div>
    );
  } else {
    return (
      <div>
        <h1>Services</h1>
        <h2>Current debt: Placeholder$</h2>
        <ul>
          {services.map((service, index) => (
            <li key={service.name}>
              <h3>{service.name}</h3>
              <div className="content">
                <p>
                  {service.description}
                  <br />
                  {service.metrics}
                  <br />
                  <br />
                  {service.price} $ per hour{" "}
                  <button onClick={updatePricing}>
                    {service.active ? "Disable" : "Activate"}
                  </button>
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
