import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addService } from '../actions/serviceActions';

import uuid from 'uuid'

class serviceModal extends Component {
    state = {
        name: ''
    }
    
    onChange = (e) => {
        console.log(e.target.value);
        this.setState({ [e.target.name]: e.target.value });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const newService = {
            id: uuid(),
            name: this.state.name
        }
        console.log(newService);
        // Add service via addServiceAction
        this.props.addService(newService);

    }

    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <label>
                        Name:
                      </label>
                    <input type="text" name="service" value={this.e.value} onChange={this.onChange}/>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }
}
const mapStateToProps = state => ({
    service: state.service
})

export default connect(mapStateToProps, { addService })(serviceModal)
