import React, { useState, useEffect } from "react";
import "./services.scss";

export default function Services() {
  const [services, setServices] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [pricing, setPricing] = useState([
    { time: 0, active: false, debt: 0 },
    { time: 0, active: false, debt: 0 },
    { time: 0, active: false, debt: 0 },
  ]);
  const [fullDebt, setFullDebt] = useState(0);

  const updatePricing = (index) => (e) => {
    fetch(`/api/service/${service[index]._id}`,{
      method: "PUT",
      body: !services[index].active
    }).then(response => response.json())
    .then(data => {
      console.log('Success:', data);
    })
    .catch((error) => {
      console.error('Error:', error);
    });;
    
    let newPrice = [...pricing];
    let newDebt = newPrice[index].debt;
    if (newPrice[index].active) {
      newDebt +=
        ((new Date().getTime() - newPrice[index].time) / 1000) *
        services[index].price;
    }
    newPrice[index] = {
      time: new Date().getTime(),
      active: !newPrice[index].active,
      debt: newDebt,
    };
    setPricing(newPrice);
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      let currentDebt = pricing.reduce((a, b) => ({ debt: a.debt + b.debt }));
      currentDebt = currentDebt.debt;
      pricing.map((price, index) => {
        if (price.time && price.active) {
          currentDebt +=
            ((new Date().getTime() - price.time) / 1000) *
            services[index].price;
        }
      });
      setFullDebt(Math.round((currentDebt + Number.EPSILON) * 100) / 100);
    }, 5000);
    return () => clearInterval(intervalId);
  });

  useEffect(() => {
    fetch("/api/service")
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setServices(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return (
      <div>
        <h1>Services</h1>
        <p>Loading...</p>
      </div>
    );
  } else {
    return (
      <div>
        <h1>Services</h1>
        <h2>Current debt: {fullDebt}$</h2>
        <ul>
          {services.map((service, index) => (
            <li key={service.name}>
              <h3>{service.name}</h3>
              <div className="content">
                <p>
                  {service.description}
                  <br />
                  {service.metrics}
                  <br />
                  <br />
                  {service.price} $ per hour{" "}
                  <button onClick={updatePricing(index)}>
                    {service.active ? "Disable" : "Activate"}
                  </button>
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
