import React, { useState, useEffect } from "react";
import "./services.scss";

export default function Services(props) {
  const [services, setServices] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [pricing, setPricing] = useState([]);

const updatePricing = service => e => {
    console.log(service);
}

  useEffect(() => {
    fetch("/api/service")
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setServices(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return (
      <div>
        <h1>Services</h1>
        <p>Loading...</p>
      </div>
    );
  } else {
    return (
      <div>
        <h1>Services</h1>
        <ul>
          {services.map((service) => (
            <li key={service.name}>
              <h3>{service.name}</h3>
              <div className="content">
                <p>
                  {service.description}
                  <br />
                  {service.metrics}
                  <br /><br/>
                  {service.price} $ per hour{" "} <button onClick={updatePricing(service)}>Activate</button>
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
