import React, { useState, useEffect } from "react";
import "./services.scss";

export default function Services(props) {
  const [services, setServices] = useState([]);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState(null);
  const [isActive, setActive] = useState([
    { name: "General purpose—Dv3", active: false, time: 0 },
    { name: "Compute optimized—Fsv2", active: false, time: 0 },
    { name: "Burstable VMs—B1S", active: false, time: 0 },
  ]);

  function handleClick(e) {
    var date = new Date().toLocaleTimeString();
    let newActive = [...isActive]
    const index = newActive.findIndex(x => x.name === e.name)
    console.log(index)
    //if(index){
    //    setActive(newActive[index].active = true);
    //}
    console.log(isActive);
    
  }

  useEffect(() => {
    fetch("/api/service")
      .then((res) => res.json())
      .then(
        (result) => {
          setIsLoaded(true);
          setServices(result);
        },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return (
      <div>
        <h1>Services</h1>
        <p>Loading...</p>
      </div>
    );
  } else {
    return (
      <div>
        <h1>Services</h1>
        <ul>
          {services.map((service) => (
            <li key={service.name}>
              <h3>{service.name}</h3>
              <div className="content">
                <p>
                  {service.description}
                  <br />
                  {service.metrics}
                  <br />
                  {service.price} $ per hour{" "}
                  <button onClick={(e) => handleClick(service, e)}>
                    Activate
                  </button>
                </p>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
