import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addService } from '../actions/serviceActions';

import uuid from 'uuid'

class serviceModal extends Component {
    state = {
        visible: false,
        name: ''
    }
    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const newService = {
            id: uuid(),
            name: this.state.name
        }

        // Add service via addServiceAction
        this.props.addService(newService);

    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label for="service">
                        Name:
                      </label>
                    <input type="text" name="service" onChange={this.onChange}/>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        );
    }
}
const mapStateToProps = state => ({
    service: state.service
})

export default connect(mapStateToProps, { addService })(serviceModal)
