import React, { Component } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import uuid from 'uuid';

import { Row, Col, Card, Space, Divider, Avatar, Modal, Button } from 'antd';
import '../index.css'

import { WindowsOutlined, PlusOutlined } from '@ant-design/icons';

class Services extends Component {
    state = {
        services: [
            { id: uuid(), name: 'Virtual Machine 1' },
            { id: uuid(), name: 'Virtual Machine 2' },
            { id: uuid(), name: 'Virtual Machine 3' },
            { id: uuid(), name: 'Virtual Machine 4' }
        ],
        visible: false,
    }

    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };



    render() {
        const { services } = this.state;

        function ListServices(props) {
            const listServices = services.map((ser) =>
                <Col xs={24} md={8} style={{ display: 'flex' }}>
                    <Card key={ser.id} style={{ width: '100%' }} title={ser.name} bordered={false} hoverable="true" onClick={this.showModal}>
                        <h3>750 Hours</h3>
                        <h5>Quad Core, 16GB RAM, 1TB Storage</h5>
                        <Divider />
                        <p>Create Windows virtual machines with on-demand capacity in seconds.</p>
                    </Card>
                </Col>
            );
            return (
                <div>{listServices}</div>
            );
        }

        return (
            <div className="serviceContainer">
                <h2>Product List</h2>
                <Divider />
                <div className="site-card-wrapper">
                    <Row gutter={[16, 16]}>
                        <Col xs={24} md={8} style={{ display: 'flex' }}>
                            <Card style={{ width: '100%' }} title="Windows VM" bordered={false} hoverable="true" onClick={this.showModal}>
                                <h3>750 Hours</h3>
                                <h5>Quad Core, 16GB RAM, 1TB Storage</h5>
                                <Divider />
                                <p>Create Windows virtual machines with on-demand capacity in seconds.</p>
                            </Card>
                        </Col>
                        <Col xs={24} md={8} style={{ display: 'flex' }}>
                            <Card style={{ width: '100%' }} title="Windows VM" bordered={false} hoverable="true" onClick={this.showModal}>
                                <h3>750 Hours</h3>
                                <h5>Quad Core, 16GB RAM, 1TB Storage</h5>
                                <Divider />
                                <p>Create Windows virtual machines with on-demand capacity in seconds.</p>
                            </Card>
                        </Col>
                        <Col xs={24} md={8} style={{ display: 'flex' }}>
                            <Card style={{ width: '100%' }} title="Windows VM" bordered={false} hoverable="true" onClick={this.showModal}>
                                <h3>750 Hours</h3>
                                <h5>Quad Core, 16GB RAM, 1TB Storage</h5>
                                <Divider />
                                <p>Create Windows virtual machines with on-demand capacity in seconds.</p>
                            </Card>
                        </Col>
                    </Row>
                    <Divider />
                    <Row gutter={[16, 16]}>
                        <Col xs={24} md={8} style={{ display: 'flex' }}>
                            <Card style={{ width: '100%' }} title="Windows VM" bordered={false} hoverable="true" onClick={this.showModal}>
                                <h3>750 Hours</h3>
                                <h5>Quad Core, 16GB RAM, 1TB Storage</h5>
                                <Divider />
                                <p>Create Windows virtual machines with on-demand capacity in seconds.</p>
                            </Card>
                        </Col>
                        <Col xs={24} md={8} style={{ display: 'flex' }}>
                            <Card style={{ width: '100%' }} title="Linux VM" bordered={false} hoverable="true" onClick={this.showModal}>
                                <h3>750 Hours</h3>
                                <h5>Quad Core, 32GB RAM, 1TB Storage</h5>
                                <Divider />
                                <p>Create Linux virtual machines with on-demand capacity in seconds.</p>
                            </Card>
                        </Col>
                        <Col xs={24} md={8} style={{ display: 'flex' }}>
                            <Card style={{ width: '100%' }} title="Oracle DB" bordered={false} hoverable="true" onClick={this.showModal}>
                                <div className="test">
                                    <h3>500GB</h3>
                                    <h5>Raid 10</h5>
                                    <Divider />
                                    <p>Create a SQL Database that delivers intelligence built-in</p>
                                </div>
                            </Card>
                        </Col>
                    </Row>
                    <Modal
                        title="Add Service"
                        visible={this.state.visible}
                        onOk={this.handleOk}
                        onCancel={this.handleCancel}
                    >
                        <p>Some form to</p>
                        <p>Validate your request</p>
                        <p>And usage</p>
                    </Modal>
                    <div>
                        <ListServices services={this.state.services}/>
                    </div>

                </div>
                {this.state.services[0].id}
            </div>
        );
    }
}
export default Services;
