import React, { Component } from "react";
import Dashboard from "./Dashboard";
import Monitoring from "./Monitoring";
import Services from "./Services";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import "./appNav.scss";

export default class AppNav extends Component {
  constructor(props) {
    super(props);
    this.handleNav = this.handleNav.bind(this)
    this.state = {
      isOpen: true,
    };
  }  

  handleNav(e){
    this.setState({
      isOpen: e.target.isOpen
    })
  }
  
  render() {
    return (
      <Router>
        <div>
          <button onClick={this.handleNav} isOpen={this.state.isOpen}>Toggle Nav</button>
          <nav className={this.state.isOpen ? "open" : "close"}>
            <ul>
              <li>
                <Link to="/">Dashboard</Link>
              </li>
              <li>
                <Link to="/monitoring">Monitoring</Link>
              </li>
              <li>
                <Link to="/services">Services</Link>
              </li>
            </ul>
            <button onClick={this.handleNav}>Toggle Nav</button>
          </nav>
          <Switch>
            <Route path="/monitoring">
              <Monitoring />
            </Route>
            <Route path="/services">
              <Services />
            </Route>
            <Route path="/">
              <Dashboard />
            </Route>
          </Switch>
        </div>
      </Router>
    );
  }
}
