import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addService } from '../actions/serviceActions';
import { Modal, Button } from 'antd';

import uuid from 'uuid'

class serviceModal extends Component {
    state = {
        visible: false,
        name: ''
    }
    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    onChange = (e) =>{
        this.setState({[e.target.name]: e.target.value});
    }

    onSubmit = (e) => {
        e.preventDefault();
        const newService = {
           id:uuid(),
           name: this.state.name 
        }

        // Add service via addServiceAction
        this.props.addService(newService);

        this.handleOk();
    }

    render() {
        return (
            <div>
                <Button type="primary" onClick={this.showModal}>
                    Open Modal
            </Button>
                <Modal
                    title="Basic Modal"
                    visible={this.state.visible}
                    onOk={this.handleOk}
                    onCancel={this.handleCancel}
                >
                    <form onSubmit={this.onSubmit}>
                        <input type="text" name="name" id="service" onChange={this.onChange} />
                        <Button onClick={this.showModal}>Add Service</Button>
                    </form>
                </Modal>
            </div>
        );
    }
}
const mapStateToProps = state => ({
    service:state.service
})

export default connect(mapStateToProps, {addService} )(serviceModal)
