import { Row, Col, Card, Space, Divider, Avatar } from 'antd';
import '../index.css'

import { WindowsOutlined } from '@ant-design/icons';

class Services extends Component {
    state = {
        services: [
            { id: uuid(), name: 'Virtual Machine 1' },
            { id: uuid(), name: 'Virtual Machine 2' },
            { id: uuid(), name: 'Virtual Machine 3' },
            { id: uuid(), name: 'Virtual Machine 4' }
        ]
    }

    render() {
        const { services } = this.state;
        return (
            <div className="serviceContainer">
                <h1>I am service</h1>
                <div className="site-card-wrapper">
    <Row gutter={[16, 16]}>
      <Col xs={24} md={8}>
        <Card title="Windows VM" bordered={false}
        actions={[

        ]}>
          <h3>750 Hours</h3>
          <h5>Quad Core, 16GB RAM, 1TB Storage</h5>
            <p>Create Windows virtual machines with on-demand capacity in seconds.</p>
        </Card>
      </Col>
      <Col xs={24} md={8}>
        <Card title="Linux VM" bordered={false}>
          Card content
        </Card>
      </Col>
      <Col xs={24} md={8}>
        <Card title="Oracle DB" bordered={false}>
          Card content
        </Card>
      </Col>
    </Row>
  </div>
            </div>
        );
    }
}
export default Services;
