import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addService } from '../actions/serviceActions';

class serviceModal extends Component {
    state = {
        visible: false,
        name: ''
    }
    showModal = () => {
        this.setState({
            visible: true,
        });
    };

    handleOk = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    handleCancel = e => {
        console.log(e);
        this.setState({
            visible: false,
        });
    };

    onChange = (e) =>{
        this.setState({[e.target.name]: e.target.value});
    }

    render() {
        return (
            <div>
                <Button type="primary" onClick={this.showModal}>
                    Open Modal
            </Button>
                <Modal
                    title="Basic Modal"
                    visible={this.state.visible}
                    onOk={this.handleOk}
                    onCancel={this.handleCancel}
                >
                    <form onSubmit={this.onSubmit}>
                        <input type="text" name="name" id="service" onChange={this.onChange} />
                    </form>
                </Modal>
            </div>
        );
    }
}
export default connect()(serviceModal)
