import React, { Component } from 'react';
import { Form, Input, Button, Checkbox } from 'antd';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { register } from '../../actions/authActions';

const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 16 },
};
const tailLayout = {
    wrapperCol: { offset: 8, span: 16 },
};



class Register extends Component {
    states = {
        name: '',
        email: '',
        password: '',
        msg: null
    };

    static propTypes = {
        isAuthenticated: PropTypes.bool,
        error: PropTypes.object.isRequired,
        register: PropTypes.func.isRequired
    }

    onChange = (e) => {

        this.setState({ [e.target.name]: e.target.value });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const {name, email, password} = this.state;
        const newUser = {
            name,
            email,
            password
        };
        this.props.register(newUser);
        
    }

    componentDidUpdate(prevProps) {
        const { error } = this.props;
        if (error !== prevProps.error) {
            if (error.id === 'REGISTER_FAIL') {
                this.state({ msg: error.msg.msg });
                this.setState({ msg: null });
            }
        }
    }
    render() {
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <label>
                        Name:
                  </label>
                    <input type="text" id="service" name="name" onChange={this.onChange} />
                    <input type="text" id="service" name="time" onChange={this.onChange} />
                    <input type="text" id="service" name="metrics" onChange={this.onChange} />
                    <input type="text" id="service" name="description" onChange={this.onChange} />
                    <input type="submit" value="Register" />
        <p>{this.state.msg}</p>
                </form>
            </div>
        );
    }
}
const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated,
    error: state.error,
})

export default connect(
    mapStateToProps,
    { register }
)(Register);
