import React, { Fragment } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { loadUser, login } from "../actions/authActions";
import "antd/dist/antd.css";
import "../index.css";
import { Layout, Menu } from "antd";
import {
  DesktopOutlined,
  PieChartOutlined,
  SettingOutlined,
  UserOutlined,
  DashboardOutlined,
} from "@ant-design/icons";
import Dashboard from "./Dashboard";
import Services from "./Services";
import Monitoring from "./Monitoring";
import Register from "./auth/Register";
import Logout from "./auth/Logout";
import Login from "./auth/Login";
import { connect } from "react-redux";
import PropTypes from "prop-types";

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

class AppNav extends React.Component {
  state = {
    collapsed: false,
  };

  static propTypes = {
    auth: PropTypes.object.isRequired,
  };

  onCollapse = (collapsed) => {
    console.log(collapsed);
    this.setState({ collapsed });
  };

  render() {
    const { isAuthenticated, user } = this.props.auth;

    const authPage = (
      <Router>
        <Layout style={{ minHeight: "100vh" }}>
          <Sider
            collapsible
            collapsed={this.state.collapsed}
            onCollapse={this.onCollapse}
          >
            <div className="logo" />
            <Menu theme="dark" defaultSelectedKeys={["1"]} mode="inline">
              <Menu.Item key="1" icon={<DashboardOutlined />}>
                <Link to="/">Dashboard</Link>
              </Menu.Item>
              <Menu.Item key="2" icon={<PieChartOutlined />}>
                <Link to="/services">Services</Link>
              </Menu.Item>
              <Menu.Item key="3" icon={<DesktopOutlined />}>
                <Link to="/monitoring">Monitoring</Link>
              </Menu.Item>
              <SubMenu key="sub2" icon={<SettingOutlined />} title="Settings">
                <Menu.Item key="3">Option 1</Menu.Item>
                <Menu.Item key="4">Option 2</Menu.Item>
              </SubMenu>
              <SubMenu key="sub1" icon={<UserOutlined />} title="User">
                <Menu.Item key="5">
                  <Link to="/register">Register</Link>
                </Menu.Item>
                <Menu.Item key="7">
                  <Link to="/">{user.name ? user.name : <p>User</p>}</Link>
                </Menu.Item>
                <Menu.Item key="6">
                  <Link to="/logout">Logout</Link>
                </Menu.Item>
              </SubMenu>
            </Menu>
          </Sider>
          <Layout className="site-layout">
            <Header className="site-layout-background" style={{ padding: 0 }} />
            <Content style={{ margin: "0 16px" }}>
              <div
                className="site-layout-background"
                style={{ padding: 24, minHeight: 360 }}
              >
                <Switch>
                  <Route path="/" exact component={Dashboard} />
                  <Route path="/services" component={Services} />
                  <Route path="/monitoring" component={Monitoring} />
                  <Route path="/register" component={Register} />
                  <Route path="/logout" component={Logout} />
                </Switch>
              </div>
            </Content>
            <Footer style={{ textAlign: "center" }}>
              Valentin Vareskic ©2020
            </Footer>
          </Layout>
        </Layout>
      </Router>
    );

    const guestPage = (
      <li>
        <Router>
        <Link to="/login">Login</Link>
        <Route path="/login" component={Login} />
        </Router>
      </li>
    );

    return <div>{isAuthenticated ? authPage : guestPage}</div>;
  }
}
const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, null)(AppNav);
